import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import Message from "../components/shared/Message";
import Loader from "../components/shared/Loader";
import { Card, CardContent, CardMedia, IconButton, Typography } from '@mui/material';
import { Box } from '@mui/system';

const SearchScreen = () => {
    const [result, setResult] = useState([])
    const [message, setMessage] = useState("");

    const searchDetails = useSelector((state) => state.searchDetails);
    const { loading, error, product } = searchDetails;

    const productInfo = useSelector((state) => state.productInfo);
    const { searchProduct } = productInfo;
    console.log("productSearchInfo", searchProduct);

    console.log("product", product);
    console.log("productName", result);

    useEffect(() => {
        if (product) {
            console.log("product available", product);
            setResult(product)
            // window.location.reload();
        } else {
            console.log("product available")
        }
    }, [product])


    return (
        <div>
            SearchScreen
            <div>
                <h2>Search Result</h2>
                {error && <Message varient="danger">{error}</Message>}

                {loading && <Loader />}
                {message && <Message variant="danger">{message}</Message>}

                {/* <h1>{product.name}</h1> */}
                {
                    result.length > 0 ?
                        <div>{result.map((products) => {
                            return (
                                <Card sx={{ display: 'flex' }}>
                                    <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                                        <CardContent sx={{ flex: '1 0 auto' }}>
                                            <Typography component="div" variant="h5">
                                                Live From Space
                                            </Typography>
                                            <Typography variant="subtitle1" color="text.secondary" component="div">
                                                Mac Miller
                                            </Typography>
                                        </CardContent>
                                    </Box>
                                    <CardMedia
                                        component="img"
                                        sx={{ width: 151 }}
                                        image="/static/images/cards/live-from-space.jpg"
                                        alt="Live from space album cover"
                                    />
                                </Card>
                            )
                        })}</div>
                        : <div>NO data</div>
                }
            </div>
        </div>
    )
}

export default SearchScreen
