import React, { useEffect, useState } from 'react';
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from 'react-icons/fa';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { listProductDetails } from '../../actions/productActions';
import styles from './style.module.css'

function Trending({ history, match }) {
  // let navigate = useNavigate();

  const [qty, setQty] = useState(1);
  const dispatch = useDispatch();
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  const [current, setCurrent] = useState(0);
  const [selectedImage, setSelectedImage] = useState(null)
  const length = slides.length;
  

  const [products, setProducts] = useState([]);
  const img="https://bitsofco.de/content/images/2018/12/broken-1.png"

  useEffect(() => {
    dispatch(listProductDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  
  function handleClick() {
    // navigate('/products')
  }

  const nextSlide = () => {
    if (current < products.length - 3) { setCurrent(current + 1) }
  }

  const Imagehandler = (index) => {
    setSelectedImage(products[index])
  }

  const prevSlide = () => {
    if (current > 0) {
      setCurrent(current - 1)
    }
  }

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }
  return (
    <>
      <div className={styles.ViewAll}>
        <div><h1>Trending</h1></div>
        <div className={styles.viewButton}><button onClick={handleClick} className={styles.viewBtn}>View All</button></div>
      </div>
      <section className={styles.slider}>
        <FaArrowAltCircleLeft className={styles.leftarrow} onClick={prevSlide} />
        {
            products.map((slide, index) => {
            return (
              <div className={index === current ? styles.slideActive : styles.slide} key={index}>
                {index === current &&
                  <div className={styles.ImageSection}>
                    <img src={slide.image} alt='travel image' onClick={() => Imagehandler(current)} className={styles.Image} />,
                    <img src={products[index + 1].image} alt='travel image' className={styles.Image} onClick={() => Imagehandler(current + 1)} />
                    <img src={products[index + 2].image} alt='travel image' className={styles.Image} onClick={() => Imagehandler(current + 2)} />
                    <img src={products[index + 3].image} alt='travel image' className={styles.Image} onClick={() => Imagehandler(current + 3)} />
                    <img src={products[index + 4].image} alt='travel image' className={styles.Image} onClick={() => Imagehandler(current + 4)} />
                  </div>}
              </div>
            )
          })
        }
        <FaArrowAltCircleRight className={styles.rightarrow} onClick={nextSlide} />
      </section>
    </>
  )
}

export default Trending
