import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Form, Row, Col, Table } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/shared/Message";
import Loader from "../components/shared/Loader";
import { getUserDetails, updateUserProfile } from "../actions/userAction";
import { listMyOrders } from "../actions/orderAction";
import { ORDER_CREATE_REQUEST } from "../constants/orderConstant";
import {Card,Typography,CardContent,CardActions,CardMedia,CardActionArea,Button, Avatar, ListItem, Divider, ListItemAvatar, ListItemText, List} from '@mui/material'
import { Box } from "@mui/system";

const ProfileScreen = ({ location, history }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
