        setName(user.name);
        setEmail(user.email);
        setPhone(user.phone);
        setCity(user.city);
        setAddress(user.address);
        setState(user.state);
        setPincode(user.pincode);
        setLandmark(user.landmark);
        setGender(user.gender);
        console.log("user", user)
      }
    }
