import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory, useParams } from "react-router-dom";
import { listProductDetails } from "../actions/productActions";
import {
  Row,
  Col,
  ListGroup,
  Image,
  ListGroupItem,
  Form,
  Container,
} from "react-bootstrap";
import { Chip, Button, Rating, Box } from "@mui/material";
import './style.css/ProductDetail.css';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
const ProductDetails = ({ history, match }) => {
  const [qty, setQty] = useState(1);
  const [isFav, setIsFav] = useState(false);
  const dispatch = useDispatch();
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;


  useEffect(() => {
    dispatch(listProductDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  const addTowishListtHandler = () => {
    history.push(`/wishlist/${match.params.id}`);
    // localStorage.getItem
    setIsFav(true)
  };
  const GoBack = () => {
    history.push('/');
    window.location.reload(true);
  }


  //lets start
  const { id } = useParams();
  const [isWishAdd, setIsWishAdd] = useState(false);
  const [wishCount, setWishCount] = useState(0);
  const [boards, setBoards] = useState({});
  const [images, setImages] = useState([]);

  const wishCountHandler = async () => {
    if (!isWishAdd) {
      setWishCount(prev => prev +1)
      await fetch(`http://localhost:8080/api/products/${boards.id}`, {
        credentials: 'include',
        method: "POST",
        body: JSON.stringify({
         liked: true
        }),
      })
      alert("찜 목록에 추가되었습니다.")
      console.log(boards)
      console.log("count+1")
    } else  {
      setWishCount(prev => prev -1)
      await fetch(`http://localhost:8080/api/products/${boards.id}`, {
        credentials: 'include',
        method: "DELETE",
        body: JSON.stringify({
          liked: false
        })
      })
      alert("찜 목록에서 삭제되었습니다.")
      console.log(boards)
      console.log("count-1")
    }
    setIsWishAdd(prev => !prev);
  }    
  

  useEffect(() => {
    (async function () {
      await axios.get(`https://27.96.131.85:8443/api/boards/${id}`, {
        withCredentials: true,
      }).then((res)=> {
        console.log(res);
        setBoards(res.data);
        setImages(res.data.boardImages);
      });

      // 게시글 정보 state에 저장

    })();
  }, []);

  return (
    <Container>
      <Button onClick={GoBack} sx={{ backgroundColor: "#343a40 !important", color: "white !important" }}>
        <i className="fas fa-arrow-left    "></i>
        &nbsp; GO BACK
      </Button>

      <Row>
        <Col md={3}>
          <Image src={product.image} alt={product.name} fluid />
        </Col>
        <Col md={6}>
          <ListGroup variant="flush">
            <ListGroupItem>
              <h3 className="heading">{product.heading}</h3><Chip label={product.inStock ? "in-stock" : "out of stock"} color="success" />
            </ListGroupItem>
            <ListGroupItem><h4>{product.name}</h4></ListGroupItem>
            <ListGroupItem>
              <div>{product.rating} Review</div>
              <Box>
                <Rating name="half-rating" defaultValue={4} readOnly />
              </Box>
            </ListGroupItem>
            <ListGroupItem>Price : &#8377;{product.price}</ListGroupItem>
            <ListGroupItem>{product.details}</ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={3}>
          <ListGroupItem>
            <Row>
              <Col>Status :</Col>
              <Col>
                {product.inStock ? "In Stock " : "out of stock"}
              </Col>
            </Row>
          </ListGroupItem>
          {product.countInStock > 0 && (
            <ListGroupItem>
              <Row>
                <Col>Qty</Col>
                <Form.Control
                  as="select"
                  value={qty}
                  onChange={(e) => setQty(e.target.value)}
                >
                  {[...Array(product.countInStock).keys()].map((x) => (
                    <option key={x + 1} value={x + 1}>
                      {x + 1}
                    </option>
                  ))}
                </Form.Control>
              </Row>
            </ListGroupItem>
          )}
          <ListGroupItem style={{ display: "flex" }}>
            <Button
              className="btn-block"
              type="button"
              onClick={addToCartHandler}
            >
              Add to cart
            </Button>
            <Button onClick={addTowishListtHandler}>{isFav ? <FavoriteIcon sx={{ color: "red", border: "px solid black" }} /> : <FavoriteBorderIcon sx={{ color: "black" }} />}</Button>
          </ListGroupItem>
        </Col>
      </Row>
    </Container>
  );
};

export default ProductDetails;
