import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@mui/material'
import { Box } from '@mui/system'
import axios from 'axios';
import React, { useRef, useState } from 'react'
import { toast } from 'react-toastify';

const ResetLinkPage = (props) => {
  const emailRef = useRef();
  const [otpData, setOtpData] = useState(props.open)

  const sendLink = async () => {
    try {
        const data = { email: emailRef.current.value }
        await axios.post("http://localhost:3000/user/forgot-password", data).then((resp) => {
          console.log(resp)
            if (resp.statusText === 'OK') {
                toast.success("Link sent on your gmail")
                setOtpData(props.onClose)

            } else {
                toast.error("Invalid email Id")
            }
        })

    } catch (error) {
        toast.error("Invalid User")
    }
}
  return (
    
       <Dialog
                open={props.open}
                onClose={props.onClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
               <Box>
                    <DialogTitle id="alert-dialog-title">
                        {"Forgot Password"}
                    </DialogTitle>
                    <DialogContent>
                        <DialogContentText id="alert-dialog-description">
                            <input variant='standard' label="Enter Email Id" type='email' name="email" ref={emailRef} placeholder="Enter Email Id"/>
                        </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={props.onClose}>Cancle</Button>
                        <Button onClick={sendLink} autoFocus>
                            Send Otp
                        </Button>
                    </DialogActions>
                </Box> 
            </Dialog>
    
  )
}

export default ResetLinkPage
