import {
  PRODUCT_LIST_REQUEST,
  PRODUCT_LIST_SUCCESS,
  PRODUCT_LIST_FAILS,
  PRODUCT_DETAILS_REQUEST,
  PRODUCT_DETAILS_SUCCESS,
  PRODUCT_DETAILS_FAILS,
  PRODUCT_SEARCH_REQUEST,
  PRODUCT_SEARCH_SUCCESS,
  PRODUCT_SEARCH_FAILS
} from "../constants/productConstant";

export const productListReducer = (state = { products: [] }, action) => {
  switch (action.type) {
    case PRODUCT_LIST_REQUEST:
      return { loading: true, products: [] };
    case PRODUCT_LIST_SUCCESS:
      return { loading: false, products: action.payload };
    case PRODUCT_LIST_FAILS:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const productDetailsReducer = (
  state = { product: { reviews: [] } },
  action
) => {
  switch (action.type) {
    case PRODUCT_DETAILS_REQUEST:
      return { loading: true, ...state };
    case PRODUCT_DETAILS_SUCCESS:
      return { loading: false, product: action.payload };
    case PRODUCT_DETAILS_FAILS:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const productSearchReducer = (
  state = { product: { reviews: [] } },
  action
) => {
  switch (action.type) {
    case PRODUCT_SEARCH_REQUEST:
      return { loading: true, ...state };
    case PRODUCT_SEARCH_SUCCESS:
      return { loading: false, product: action.payload };
    case PRODUCT_SEARCH_FAILS:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const trySearch =(
  state = { product: { reviews: [] } },
  action
)=>{
  switch(action.type){
    case PRODUCT_SEARCH_REQUEST: {
      const searchValue = action.payload?.toLowerCase();
      return {
        ...state,
        product: {
          ...state.product,
          loading: false,
          isSearchActive: !!action.payload.length > 0 || false,
          foundContacts: state.product.data.filter((item) => {
            try {
              return (
                item.name.toLowerCase().search(searchValue) !== -1 ||
                item.company.toLowerCase().search(searchValue) !== -1
              );
            } catch (error) {
              return [];
            }
          }),
        },
      };
    }
    default:
      return state;
  }
  }
