import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Form, Row, Col, Table } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/shared/Message";
import Loader from "../components/shared/Loader";
import { getUserDetails, updateUserProfile } from "../actions/userAction";
import { listMyOrders } from "../actions/orderAction";
import { ORDER_CREATE_REQUEST } from "../constants/orderConstant";
import { Card, Typography, CardContent, CardActions, CardMedia, CardActionArea, Button, Avatar, ListItem, ListItemAvatar, ListItemText, Divider, List, Snackbar, Alert } from '@mui/material'
import { Box } from "@mui/system";
import PhoneEnabledIcon from '@mui/icons-material/PhoneEnabled';
import HomeIcon from '@mui/icons-material/Home';
import LocationCityIcon from '@mui/icons-material/LocationCity';
import HowToRegSharpIcon from '@mui/icons-material/HowToRegSharp';
import HomeWorkSharpIcon from '@mui/icons-material/HomeWorkSharp';
import ApartmentIcon from '@mui/icons-material/Apartment';
import PinDropIcon from '@mui/icons-material/PinDrop';
import AddLocationAltIcon from '@mui/icons-material/AddLocationAlt';
import MapsHomeWorkIcon from '@mui/icons-material/MapsHomeWork';

const ProfileScreen = ({ location, history }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [city, setCity] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("");
  const [pincode, setPincode] = useState("");
  const [landmark, setLandmark] = useState("");
  const [gender, setGender] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const [getUserFirstLetter, setGetUserFirstLater] = useState('');
  const [open, setOpen] = useState(false);

  const dispatch = useDispatch();
  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { success } = userUpdateProfile;

  const orderListMy = useSelector((state) => state.orderListMy);
  const { loading: loadingOrders, orders, error: errorOrders } = orderListMy;

  useEffect(() => {
    if (!userInfo) {
      history.push("/login");
    } else {
      if (!user.name) {
        dispatch(getUserDetails("profile"));
        dispatch(listMyOrders());
        console.log("user Data", user)
      } else {
        setName(user.name);
        setEmail(user.email);
        setPhone(user.phone);
        setCity(user.city);
        setAddress(user.address);
        setState(user.state);
        setPincode(user.pincode);
        setLandmark(user.landmark);
        setGender(user.gender);
        setGetUserFirstLater(user.email[0].toUpperCase())
      }
    }
  }, [history, userInfo, user, dispatch]);

  const submitHandler = (e) => {
    e.preventDefault();
    //dispatch
    dispatch(updateUserProfile({ id: user._id, name, email, phone, city, address, state, pincode, landmark, gender, password }));
    // window.location.reload(false)
    // alert("profile updated")
    setOpen(true)
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  return (
    <>
      <Row style={{ width: "100%" }}>
        <Col md={3}>
          <h1>Update Information</h1>
          {error && <Message varient="danger">{error}</Message>}
          {success && <Snackbar open={open} autoHideDuration={10000} >
            <Alert sx={{backgroundColor:"green"}} severity="success" sx={{ width: '100%' }}>
              Profile updated successfully
            </Alert>
          </Snackbar>}
          {loading && <Loader />}
          {message && <Message variant="danger">{message}</Message>}
          <Form onSubmit={submitHandler}>
            <Form.Group controlId="email">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                type="email"
                placeholder="enter email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="phone">
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="phone"
                placeholder="enter phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="city">
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter city"
                value={city}
                onChange={(e) => setCity(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="address">
              <Form.Label>Address</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="state">
              <Form.Label>State</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter state"
                value={state}
                onChange={(e) => setState(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="pincode">
              <Form.Label>Pincode</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter pincode"
                value={pincode}
                onChange={(e) => setPincode(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="landmark">
              <Form.Label>Landmark</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter landmark"
                value={landmark}
                onChange={(e) => setLandmark(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="gender">
              <Form.Label>Gender</Form.Label>
              <Form.Control
                type="text"
                placeholder="enter gender"
                value={gender}
                onChange={(e) => setGender(e.target.value)}
              ></Form.Control>
            </Form.Group>
            {/* <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="confirmPassword">
              <Form.Label>COnfirm Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Re-enter password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              ></Form.Control>
            </Form.Group> */}
            <Button type="submit" varient="primary">
              Update
            </Button>
          </Form>
        </Col>
        <Col md={9}>
          <h3 className="text-dark">Information</h3>
          <Card sx={{ maxWidth: "100%", boxShadow: "1px 1px 30px 1px gray" }}>
            <Box sx={{ display: "flex" }}>
              <Box sx={{ width: "30%", backgroundColor: "#394165", display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column" }}>
                <Avatar
                  sx={{ bgcolor: "#394165", color: "white", width: "80px", height: "80px", fontSize: "50px", boxShadow: "3px 3px 150px 1px white" }}
                  alt="Remy Sharp"
                  src="/broken-image.jpg"
                >
                  {getUserFirstLetter}
                </Avatar>
                <Box sx={{ color: "white", fontWeight: "bold", mt: 2, fonztSize: "20px" }}>
                  {user.name ? user.name : "Not Available"}
                </Box>
                <Box sx={{ color: "white", fontWeight: "bold", mt: 2, fonztSize: "20px" }}>
                  {user.email ? user.email : "Not Available"}
                </Box>
              </Box>
              <CardContent>
                <Box component="div" sx={{ display: "flex" }}>
                  <Box>
                    <Typography gutterBottom variant="h5" component="div">
                      Email
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {user.email ? user.email : "Not Available"}
                    </Typography>
                  </Box>
                  <Box sx={{ position: "relative", left: "100px" }}>
                    <Typography gutterBottom variant="h5" component="div">
                      phone
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {user.phone ? user.phone : "Not Available"}
                    </Typography>
                  </Box>
                </Box>
                <Box component="div" sx={{ display: "flex", mt: 3 }}>
                  <Box>
                    <Typography gutterBottom variant="h5" component="div">
                      Gender
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {user.gender ? user.gender : "Not Available"}
                    </Typography>
                  </Box>
                </Box>
                <Box sx={{ mt: 5 }}>
                  <h2>Address</h2>
                </Box>
                <List
                  sx={{
                    width: '100%',
                    maxWidth: 360,
                    bgcolor: 'background.paper',
                    display: "flex"
                  }}
                >
                  <ListItem>
                    <ListItemAvatar>
                      <PinDropIcon />
                    </ListItemAvatar>
                    <ListItemText primary="Pincode" secondary={user.pincode ? user.pincode : "Not Available"} />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                  <ListItem>
                    <ListItemAvatar>
                      <LocationCityIcon />
                    </ListItemAvatar>
                    <ListItemText primary="City" secondary={user.city ? user.city : "Not Available"} />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                  <ListItem>
                    <ListItemAvatar>
                      <ApartmentIcon />
                    </ListItemAvatar>
                    <ListItemText sx={{ width: "500px" }} primary="State" secondary={user.state ? user.state : "Not Available"} />
                  </ListItem>
                </List>
                <List
                  sx={{
                    width: '100%',
                    maxWidth: 360,
                    bgcolor: 'background.paper',
                    display: "flex"
                  }}
                >
                  <Divider variant="inset" component="li" />
                  <ListItem>
                    <ListItemAvatar>
                      <AddLocationAltIcon />
                    </ListItemAvatar>
                    <ListItemText primary="landmark" secondary={user.landmark ? user.landmark : "Not Available"} />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                  <ListItem>
                    <ListItemAvatar>
                      <MapsHomeWorkIcon />
                    </ListItemAvatar>
                    <ListItemText sx={{ width: "500px" }} primary="Home Address" secondary={user.address ? user.address : "Not Available"} />
                  </ListItem>
                </List>
              </CardContent>
            </Box>
          </Card>
        </Col>
        {/* <Col md={9}>
          <h1>My Orders</h1>
          {loadingOrders ? (
            <Loader />
          ) : errorOrders ? (
            <Message variant="danger">{errorOrders}</Message>
          ) : (
            <Table striped bordered hover responsive className="table-sm">
              <thead>
                <tr>
                  <td>ID</td>
                  <td>DATE</td>
                  <td>TOTAL</td>
                  <td>PAID</td>
                  <td>DELIVERD</td>
                  <td></td>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order._id}>
                    <td>{order._id}</td>
                    <td>{order.createdAt.substring(0, 10)}</td>
                    <td>{order.totalPrice}</td>
                    <td>
                      {order.isPaid ? (
                        order.paidAt.substring(0, 10)
                      ) : (
                        <i
                          className="fas fa-times"
                          style={{ color: "red" }}
                        ></i>
                      )}
                    </td>
                    <td>
                      {order.isDeleverd ? (
                        order.deleverdAt.substring(0, 10)
                      ) : (
                        <i
                          className="fas fa-times"
                          style={{ color: "red" }}
                        ></i>
                      )}
                    </td>
                    <td>
                      <LinkContainer to={`/order/${order._id}`}>
                        <Button variant="light">Details</Button>
                      </LinkContainer>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Col> */}
      </Row>
    </>
  );
};

export default ProfileScreen;
